     let errors = xhr.responseJSON.errors;                    let errorText = '';                                $.each(errors, function (key, value) {                        errorText += value[0] + '<br>';                        $('#' + key).text(value[0]);                    });                                Swal.fire({                        icon: 'error',                        title: 'Validation Error',                        html: errorText                    });                } else {                    Swal.fire({                        icon: 'error',                        title: 'Oops!',                        text: 'Something went wrong. Please try again.'                    });                }            }        });    });});</script>@endsection